# %% ../nbs/10_join.ipynb 6
def charjoin(char: str, *strs) -> str: 
    return char.join(tuple(argtup(strs)))

def splitjoin(s: str, sep: str = DOT, char: str = U1) -> str:
    return char.join(s.split(sep))
    
def stripjoin(*args: str, sep: str = DOT) -> str:
    return sep.join([s.strip(sep) for s in args])

# %% ../nbs/10_join.ipynb 7
def dotstr(*strs) -> str:  return charjoin(DOT, *strs)

def dashstr(*strs) -> str: return charjoin(DASH, *strs)

def ustr(*strs) -> str:    return charjoin(U1, *strs)

def dustr(*strs) -> str:   return charjoin(U2, *strs)

def nilstr(*strs) -> str:  return charjoin(NIL, *strs)
