gpu_program.raw_data_vs:
	#version 150
	in vec4 gxl3d_Position;
	in vec4 gxl3d_Color;
	uniform mat4 gxl3d_ModelViewProjectionMatrix;

	out Vertex
	{
	  vec4 color;
	} vertex;

	void main()
	{
	  gl_Position = gxl3d_ModelViewProjectionMatrix * gxl3d_Position;
	  vertex.color =  gxl3d_Color;
	}

gpu_program.raw_data_ps:
	#version 150
	in Vertex
	{
	  vec4 color;
	} vertex;

	out vec4 FragColor;
	void main()
	{
	  FragColor = vertex.color;
	}
